document.addEventListener("DOMContentLoaded",()=>{if(document.querySelector("#main")&&document.querySelector("#main").addEventListener("mousemove",e=>{document.querySelector("#main-text").style.width=e.clientX/2+"px",document.querySelector("#up").style.width="calc(1000px + "+e.clientX+"px)"}),document.querySelector("#comingSoon")){let e=document.querySelector("#comingSoon");for(let t=0;t<35;t++)e.innerHTML+='<div class="box"></div>';let t=document.querySelectorAll(".box");for(let e=0;e<t.length;e++)t[e].style.animation=`forBox 5s cubic-bezier(.36,0,.64,.99)  ${e/100}s infinite alternate`}});