    <title>Document</title>
    <style>
        html{font-size:10px}body{margin:0;padding:0;background:#101114;display:-webkit-box;display:-ms-flexbox;display:-webkit-flex;display:flex;-webkit-box-pack:center;-ms-flex-pack:center;-webkit-justify-content:center;justify-content:center;height:99.1rem}#wrapper{position:relative;top:7.9rem;min-width:300px;width:96rem;height:83.2rem;display:-ms-grid;display:grid;-ms-grid-columns:auto auto;grid-template-columns:auto auto}#wrapper #main{position:absolute;top:0;left:32.1rem;right:31.8rem;width:63.9rem;height:56.8rem;background:#f7f8f8;border-top:1px solid #e8e9e9}#wrapper #main h1{font-size:2.45rem;color:#383d43;top:16.5rem;font-family:OpenSans,sans-serif;font-weight:700}#wrapper #main h1,#wrapper #main p{position:absolute;left:2.9rem;-webkit-transform:translateY(-100%);-ms-transform:translateY(-100%);transform:translateY(-100%)}#wrapper #main p{color:#8494ab;font-size:1.6rem;font-family:Arial,Helvetica,sans-serif}#wrapper #main .pay__info{top:4.7rem}#wrapper #main .pay__number{top:8.6rem}#wrapper #main .pay__amount{top:11.3rem}#wrapper #main .pay__card{top:23.3rem;left:4.5rem}#wrapper #main .pay__validity{top:31.3rem;left:4.5rem}#wrapper #main .pay_cvv{top:31.5rem;left:44.1rem}#wrapper #main #number{color:#373c43;left:18.8rem;top:8.6rem}#wrapper #main #amount{color:#373c43;left:18.8rem;top:11.3rem}#wrapper #main select{position:absolute;border:1px solid #e4e9ee;height:4.2rem;width:6.8rem;padding-left:1.4rem;top:33.4rem;left:4.4rem;font-size:1.6rem}#wrapper #main #years__sel{width:8.4rem;left:12.3rem}#wrapper #main .visual{position:absolute;height:23.4rem;width:36.8rem;border:1px solid #e4e9ee;border-radius:.8rem;right:4.6rem;bottom:10.5rem}#wrapper #main .visual:before{content:"";position:absolute;height:4.2rem;width:21.4rem;background:#e4e9ee;top:2.6rem;right:0}#wrapper #main .visual:after{content:"";position:absolute;height:23.4rem;width:34.8rem;border:1px solid #e4e9ee;border-radius:.8rem;right:21.4rem;top:-2rem;background:#f7f8f8}#wrapper #main .card__number{position:absolute;width:28.7rem;height:4.2rem;display:-webkit-box;display:-ms-flexbox;display:-webkit-flex;display:flex;-webkit-box-pack:justify;-ms-flex-pack:justify;-webkit-justify-content:space-between;justify-content:space-between;left:4.4rem;top:25.4rem}#wrapper #main .card__number input{border:1px solid #e4e9ee;height:4rem;width:5rem;padding-left:1.5rem;font-size:1.6rem}#wrapper #main .__5{position:absolute;height:4rem;width:10.6rem;top:33.4rem;right:6.1rem;border:1px solid #e4e9ee;padding-left:3rem;font-size:2.2rem;color:#222}#wrapper #main .abbr__5{position:absolute;height:2.4rem;width:2.4rem;top:34.3rem;right:7rem;font-size:2rem;display:-webkit-box;display:-ms-flexbox;display:-webkit-flex;display:flex;-webkit-box-pack:center;-ms-flex-pack:center;-webkit-justify-content:center;justify-content:center;color:#c1c8d1;border:1px solid #c1c8d1;border-radius:1.25rem;cursor:pointer}#wrapper #main .card__owner{position:absolute;height:4rem;width:30.5rem;left:4.4rem;bottom:13.9rem;border:1px solid #e4e9ee;padding-left:1.2rem;font-size:1.6rem}#wrapper #main #pay__button{position:absolute;display:-webkit-box;display:-ms-flexbox;display:-webkit-flex;display:flex;-webkit-box-pack:center;-ms-flex-pack:center;-webkit-justify-content:center;justify-content:center;-webkit-box-align:center;-ms-flex-align:center;-webkit-align-items:center;align-items:center;height:4rem;width:13.3rem;border-radius:8.65rem;background:#005abf;background:-webkit-gradient(linear,left top,left bottom,from(0),color-stop(#004ca2),to(#005abf));background:-webkit-linear-gradient(0,#004ca2,#005abf);background:-o-linear-gradient(0,#004ca2,#005abf);background:linear-gradient(0,#004ca2,#005abf);color:#fff;font-size:1.4rem;bottom:4.5rem;left:4.4rem;font-family:OpenSans,sans-serif;font-weight:700;cursor:pointer}#wrapper #main .touch{background:#fff!important;color:#005abf!important}#wrapper .nav{position:relative;left:0;display:-ms-grid;display:grid;-ms-grid-columns:1fr;grid-template-columns:1fr;height:37.6rem;width:31.8rem;font-size:1.8rem;overflow:hidden;font-family:OpenSans,sans-serif;font-weight:700}#wrapper .nav .nav__bar{position:relative;display:none;left:22.6rem;height:4.6rem;width:4.6rem;padding:0;margin:0;z-index:9}#wrapper .nav .nav__bar .nav__rail{position:absolute;height:4.6rem;width:4.6rem;background:#1f2229;border-radius:.5rem;padding:0;margin:0;right:4.6rem;top:0;-webkit-transition:.34s;-o-transition:.34s;transition:.34s;-webkit-animation-fill-mode:forwards;animation-fill-mode:forwards}#wrapper .nav .nav__bar .nav__sign__mid{top:2.2rem}#wrapper .nav .nav__bar .nav__sign__mid,#wrapper .nav .nav__bar .nav__sign__top{position:absolute;padding:0;margin:0;height:.3rem;width:3rem;background:#fff;-webkit-transition:-webkit-transform .6s;transition:-webkit-transform .6s;-o-transition:transform .6s;transition:transform .6s;transition:transform .6s,-webkit-transform .6s;z-index:99;-webkit-transform-origin:50% 50%;-ms-transform-origin:50% 50%;transform-origin:50% 50%;right:3.8rem}#wrapper .nav .nav__bar .nav__sign__top{top:1.2rem}#wrapper .nav .nav__bar .nav__sign__bot{position:absolute;padding:0;margin:0;height:.3rem;width:3rem;background:#fff;-webkit-transition:-webkit-transform .6s;transition:-webkit-transform .6s;-o-transition:transform .6s;transition:transform .6s;transition:transform .6s,-webkit-transform .6s;z-index:99;-webkit-transform-origin:50% 50%;-ms-transform-origin:50% 50%;transform-origin:50% 50%;top:3.2rem;right:3.8rem}#wrapper .nav .list{position:relative;height:7.5rem;width:31.8rem;padding-left:2.9rem;display:-webkit-box;display:-ms-flexbox;display:-webkit-flex;display:flex;-webkit-box-align:center;-ms-flex-align:center;-webkit-align-items:center;align-items:center;-webkit-box-sizing:border-box;box-sizing:border-box;-webkit-transition:-webkit-transform .34s;transition:-webkit-transform .34s;-o-transition:transform .34s;transition:transform .34s;transition:transform .34s,-webkit-transform .34s;margin:0;z-index:99;left:10px}#wrapper .nav .list:hover{border-left:.5rem solid #101114;padding-left:2.4rem}#wrapper .nav .nav__cabinet,#wrapper .nav .nav__history{background:#1f2229;color:#fff}#wrapper .nav .nav__payment{height:7.6rem;border-top:1px solid #242730;background:#2a2d37;color:#808592}#wrapper .nav .nav__options{background:#191c24;color:#fff;border-bottom:1px solid #20232b}#wrapper .nav .nav__quit{background:#191c24;color:#808592}#wrapper .article{position:absolute;left:32.1rem;bottom:0;width:63.9rem;height:26.2rem;background:#373c43;color:#808793;border-bottom:1px solid #272b30;padding:2.9rem;-webkit-box-sizing:border-box;box-sizing:border-box;font-size:1.6rem}@media screen and (max-width:1000px){html{font-size:7px}}@media screen and (max-width:700px){#wrapper{width:63.9rem;border:2px solid}#wrapper #main{left:0}#wrapper .nav{height:42rem;left:27.5rem;top:4.6rem}#wrapper .nav .nav__bar{display:-webkit-box;display:-ms-flexbox;display:-webkit-flex;display:flex}#wrapper .nav div{-webkit-transform:translate(100%);-ms-transform:translate(100%);transform:translate(100%)}#wrapper .nav .nav__quit{border-radius:0 0 3rem 0}#wrapper .article{left:0}}@media screen and (max-width:500px){#wrapper{width:44rem}#wrapper .nav{left:10rem;top:2.2rem}#wrapper #main{width:44rem;overflow:hidden}#wrapper #main .pay_cvv{left:24rem}#wrapper #main .visual,#wrapper #main .visual:before{left:16.46rem}#wrapper #main .visual:after{left:-13rem}#wrapper .article{width:44rem;height:auto}}
    </style>
</head>
<body>

<div id="wrapper">

    <!-- Navigation -->
    <nav class="nav">
        <div class="prev nav__bar">
            <div class="prev nav__sign__top"></div>
            <div class="prev nav__sign__mid"></div>
            <div class="prev nav__sign__bot"></div>
            <div class="prev nav__rail"></div>
        </div>
        <div class="prev list nav__cabinet">Личный кабинет</div>
        <div class="prev list nav__payment">Платежи (активная)</div>
        <div class="prev list nav__history">История платежей</div>
        <div class="prev list nav__options">Настройки</div>
        <div class="prev list nav__quit">Выйти</div>
    </nav>  

    <!-- Main cont containing validation form :START:-->
    <main id="main">
        <p class="pay__info">Информация по оплате</p>
        <p class="pay__number">Номер счета: 
            <p id="number">87123658716587</p> 
        </p>    
        <p class="pay__amount">Сумма платежа: 
            <p id="amount">100руб.</p> 
        </p>

        <h1 class="h1">Данные банковской карты</h1>

        <div id="form">
            <div class="visual"></div>
            <form action="">

                <!-- Card number -->
                <p class="pay__card">Номер карты</p>
                <div class="card__number">
                    <input type="text" class="num __1">
                    <input type="text" class="num __2">
                    <input type="text" class="num __3">
                    <input type="text" class="num __4">
                </div>

                <!-- CVV2/CVC2 -->
                <p class="pay_cvv">Код CVV2/CVC2 </p>
                <input type="text" name="cvv2__cvc2" class="num __5">
                <abbr title="???" class="abbr__5">?</abbr>

                <!-- Date -->
                <p class="pay__validity">Срок действия</p>
                <select name="months" id="months__sel">
                    <option value="01">01</option>
                    <option value="01">02</option>
                    <option value="01">03</option>
                    <option value="01">04</option>
                    <option value="01">05</option>
                    <option value="01">06</option>
                    <option value="01">07</option>
                    <option value="01">08</option>
                    <option value="01">09</option>
                    <option value="01">10</option>
                    <option value="01">11</option>
                    <option value="01">12</option>
                </select>
                <select name="years" id="years__sel">
                        <!-- JS going to fill it  -->
                </select>
 
                <!-- Card owner -->
                <input type="text" class="card__owner" placeholder="Держатель карты (только на русском)">

                <!-- Submit -->
                <div id="pay__button">Оплатить</div>
            </form>
        </div>
    </main>
    <!-- Main cont containing validation form :FINISH:-->

    <!-- Third block containing some text -->
    <div class="article">
        
        Далеко-далеко за словесными горами в стране гласных и согласных живут рыбные тексты. Наш то переулка грустный которое, правилами встретил первую снова? Собрал, своего жаренные приставка буквоград последний агентство вдали несколько океана его.
        <br><br>
        Далеко-далеко за словесными горами в стране гласных и согласных живут рыбные тексты. Страну, ему!
        <br><br>
        Далеко-далеко за словесными горами в стране гласных и согласных живут рыбные тексты. Текст над своих послушавшись силуэт бросил, домах которой до буквоград вершину деревни реторический одна моей эта? Маленькая, то!
    </div>

</div>
<!-- Wrapper's end -->

    <script src="js/validation.js"></script>
</body>
